#!/usr/bin/env node

var _ = require('underscore'),
	async = require('async'),
	rigging = require('../lib/compile.js');

// Usage:
//
//	rigging srcDirectoryOrFile [srcDirectoryOrFile_2] ... [srcDirectoryOrFile_n]
//
var sources = process.argv;

// Remove node and script name parameters
sources.shift();
sources.shift();

// Compile sources
rigging = rigging();
rigging.compile(sources,{
	outputPath: "./.rigging_out",
	cmdLine: true
}, function (err) {
	console.log("Compilation complete!");
});